                    $diskon         = $sql_medc_det->harga - $disk3;
                    $subtotal       = ($disk3 - (isset($sql_medc_det->potongan) ? $sql_medc_det->potongan : 0)) * $jml_akhir;
                    
                    $data = [
                        'tgl_simpan'        => date('Y-m-d H:i:s'),
