                $diskon     = ((float)$harga - (float)$disk3) * (int)$jml;
                $subtotal   = (((float)$disk3 - (float)$potongan) * (int)$jml) - (float)$potongan_poin;
                
                $data = [
                    'tgl_modif'     => date('Y-m-d H:i:s'),
